import { List, Box, Text, Button } from "@chakra-ui/react";
import useGetAllBooks from "../queries/getAllBooks";
import ListBookRow
 from "./ListBookRow";
 import {useRecoilValue} from 'recoil';
import { authorSearch, genreSearch, titleSearch } from "../atom/store";

const ListOfBooks: React.FC =() => {
    let title = useRecoilValue(titleSearch);
    let author = useRecoilValue(authorSearch);
    let genre = useRecoilValue(genreSearch);
    const {data: bookDetails} = useGetAllBooks(title, author, genre);
    // console.log('what is title: ', title)
    // console.log('what is author: ', author)

    // console.log('what is genre: ', genre)

    if (bookDetails){
        console.log('in here ever?')
        console.log(bookDetails[0].author)

    }else {
        console.log('why')
    }
    const handleClick = () => {
        console.log('hello world!')
    }
return(

    <List>
        <Box>
            {
                bookDetails && bookDetails.map((book, index: number) => (
                    <ListBookRow 
                        handleClick={handleClick}
                        key={index}
                        ID={book.ID}
                        title={book.title}
                        description={book.description}
                        author={book.author}
                        price={book.price} 
                        genre={book.genre}                    
                    />        
                ))
            }
        </Box>
    </List>
)
}

export default ListOfBooks;
