import { Formik } from "formik";
import useGetBooks from "../queries/getBooks";
import { useRecoilState, useRecoilValue } from "recoil";
import { titleSearch, priceSearch, authorSearch } from "../atom/store";
//title, author, genre, as search parameters
const Form: React.FC =() => {
    //const [title, setTitle] = useRecoilState(titleSearch);
    const titleValue = useRecoilValue(titleSearch)
    const authorValue = useRecoilValue(authorSearch)
    const priceValue = useRecoilValue(priceSearch)
    return(
        <Formik
        initialValues={{ title: titleValue, author: authorValue, genre: priceValue}}
        onSubmit={(values, {setSubmitting }) => {
            setTimeout(() => {
                alert(JSON.stringify(values, null, 2));
                setSubmitting(false);
            }, 400);
            useGetBooks(values.author, values.genre, values.title);

        }}
    >
        {({
            values,
            errors,
            touched, 
            handleChange,
            handleSubmit,
            isSubmitting,
        }) => (
            <form onSubmit={handleSubmit}>
                <input
                    type="title"
                    name="title"
                    onChange={handleChange}
                    value={values.title}
                />
                <input
                type="author"
                name="author"
                onChange={handleChange}
                value={values.author}
                />
                <input
                type="genre"
                name="genre"
                onChange={handleChange}
                value={values.genre}
                />
                <button type="submit" disabled={isSubmitting}>
                    Search
                </button>
            </form>
        )}
    </Formik>
    )
}


export default Form;
