// export default useGetAllBooks;

const useGetAllBooks = (
  options: UseQueryOptions<BookDetails[],string> = {}
): UseQueryResult<BookDetails[],string> =>
  useQuery<BookDetails[], string>(
    [useGetAllBookKey],
    async () =>
      (await axiosInstance.get<{ result: BookDetails[] }>(`/books`)).data.result,
      options
  );

export default useGetAllBooks;
